    <>
    <Routes> 
      <Route path="/" element={<LoginPage/>} />
      <Route path="selectGame" element={<InitialPage/>} />
      <Route path="selectGame/Partita" element={<GameField/>}  />
      <Route path="selectGame/Regole" element={<Rules/>}  />
    </Routes>
    <div style={{height:"0px"}}>
        <ToastContainer/>
    </div>
  </>
  );
}

export default App;
