import React from 'react'
import {Link} from 'react-router-dom'
import {socket} from '../components/LoginPage.js'

export function InitialPage() {
    return (
    <>
    <div className="d-grid gap-2 d-md-block">
        <button className="btn btn-primary" type="button" onClick={() => gameTypeSelected("single")}><Link to="./partita">Single Player</Link></button>
        <button className="btn btn-primary" type="button" onClick={() => gameTypeSelected("multi")}>Random multiplayer</button>
        <button className="btn btn-primary" type="button" onClick={() => gameTypeSelected("friend")}>Play with a friend</button>
    </div>
    </>
  )
}


function gameTypeSelected(modalità){
  socket.emit('gameTypeSelected', modalità);
  // Nell'attesa della risposta (evento socket partitaIniziata) si potrebbe rendere visibile il simbolo di attesa
}
